# Calculate the cost per flower for each package size. cost_per_flower_3 = 2.50 / 3 cost_per_flower_2 = 1 / 2 print(f'{cost_per_flower_3=}') print(f'{cost_per_flower_2=}') # Calculate the total cost of buying 18 flowers using the cheaper package size. num_packages_2 = 18 / 2 total_cost_2 = num_packages_2 * 1 print(f'{num_packages_2=}') print(f'{total_cost_2=}') # Calculate the total cost of buying 18 flowers using the more expensive package size. num_packages_3 = 18 / 3 total_cost_3 = num_packages_3 * 2.50 print(f'{num_packages_3=}') print(f'{total_cost_3=}') # Calculate the difference in cost between the two options. savings = total_cost_3 - total_cost_2 print(f'{savings=}')